import {
	Box,
	Table,
	TableBody,
	TableCell,
	TableContainer,
	TableFooter,
	TableHead,
	TablePagination,
	TableRow,
} from '@mui/material';
import { useEffect, useState } from 'react';

const desired_data_fields = [
	'dividendYield',
	'fiveYearAverageDividendYield',
	'forwardPE',
	'grossMargins',
	'pegRatio',
	'fiftyTwoWeekLow',
	'fiftyTwoWeekHigh',
	'52WeekChange',
];

export const App = () => {
	const [data, setData] = useState(null);

	const getData = () => {
		fetch('/data')
			.then((newData) => newData.json())
			.then((d) => {
				console.log(d.data);

				setData(d.data);
			});
	};
	useEffect(() => {
		getData();
	}, []);
	return (
		<Box
			style={{
				width: '100vw',
				minHeight: '100vh',
				display: 'flex',
				flexDirection: 'column',
				alignItems: 'center',
				justify: 'center',
			}}
		>
			<div style={{ position: 'absolute', top: '5vh', fontSize: 18 }}>
				S&P 500 Index Data
			</div>
			{data && <DataTable data={data} />}
		</Box>
	);
};

const DataTable = ({ data }) => {
	const [page, setPage] = useState(0);
	const [rowsPerPage, setRowsPerPage] = useState(10);

	let sym = ['Symbol'];
	const columns = sym.concat(
		Object.keys(data[0][1]).filter((c) => {
			return desired_data_fields.includes(c);
		})
	);

	const handleChangePage = (event, newPage) => {
		setPage(newPage);
	};

	const handleChangeRowsPerPage = (event) => {
		setRowsPerPage(+event.target.value);
		setPage(0);
	};

	const renderRows = () => {
		return data
			.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
			.map((row) => {
				return (
					<TableRow>
						<TableCell
							style={{
								fontWeight: 'bold',
							}}
						>
							{row[1]['shortName']}
						</TableCell>
						{columns.slice(1, columns.length).map((c, idx) => {
							return (
								<TableCell
									style={{
										padding: 'none',
										background: 'white',
										height: '2rem',
									}}
									key={idx}
								>
									{row[1][c]}
								</TableCell>
							);
						})}
					</TableRow>
				);
			});
	};

	const renderHeader = () => {
		return columns.map((c, idx) => {
			return <TableCell key={idx}>{c}</TableCell>;
		});
	};
	return (
		<TableContainer
			style={{
				width: '80vw',
				height: '70vh',
				marginTop: '10vh',
				border: 'solid',
				borderRadius: 15,
				padding: '10 0 10 10',
				borderColor: 'gray',
			}}
		>
			<Table stickyHeader>
				<TableHead>
					<TableRow>{renderHeader()}</TableRow>
				</TableHead>

				<TableBody>{renderRows()}</TableBody>
				<TableFooter>
					<TableRow>
						<TablePagination
							rowsPerPageOptions={[5, 10, 25, 50, 100]}
							component="div"
							count={data.length}
							rowsPerPage={rowsPerPage}
							page={page}
							onPageChange={handleChangePage}
							onRowsPerPageChange={handleChangeRowsPerPage}
						/>
					</TableRow>
				</TableFooter>
			</Table>
		</TableContainer>
	);
};
