import {
	Box,
	Table,
	TableBody,
	TableCell,
	TableContainer,
	TableFooter,
	TableHead,
	TablePagination,
	TableRow,
	Menu,
	MenuItem,
	Button,
} from '@mui/material';
import Fade from '@mui/material/Fade';
import { sortBy } from 'lodash';
import PopupState, { bindMenu, bindTrigger } from 'material-ui-popup-state';

import React, { useEffect, useState } from 'react';

const desired_data_fields = [
	'dividendYield',
	'fiveYearAverageDividendYield',
	'forwardPE',
	'trailingPE',
	'grossMargins',
	'pegRatio',
	'fiftyTwoWeekLow',
	'fiftyTwoWeekHigh',
	'52WeekChange',
];

export const App = () => {
	const [data, setData] = useState(null);
	const [sortByLabel, setSortByLabel] = useState(null);
	const [sortedData, setSortedData] = useState(null);

	useEffect(() => {
		if (data && sortByLabel != null) {
			sortData(data, sortByLabel);
		}
	}, [sortByLabel]);

	const sortData = (data, column) => {
		const newData = sortBy(data, (d) => [d[1][column]]);
		setSortedData(newData);
	};

	const getData = () => {
		fetch('/data')
			.then((newData) => newData.json())
			.then((d) => {
				console.log(d.data);

				setData(d.data);
			});
	};
	useEffect(() => {
		getData();
	}, []);

	return (
		<Box
			style={{
				width: '100vw',
				minHeight: '100vh',
				display: 'flex',
				flexDirection: 'column',
				alignItems: 'center',
				justify: 'center',
			}}
		>
			<div
				style={{
					position: 'absolute',
					top: '5vh',
					fontSize: 20,
					fontWeight: 'semibold',
				}}
			>
				S&P 500 Index Data
			</div>
			<Box
				style={{
					display: 'flex',
					flexDirection: 'column',
					alignItems: 'center',
					marginTop: '10vh',
					height: '80vh',
				}}
			>
				{data && (
					<>
						<Box
							style={{
								display: 'flex',
								width: '100%',
								flexDirection: 'row',
								flex: 1,
								alignItems: 'center',
								justifyContent: 'start',
							}}
						>
							<SortMenu
								sortByLabel={sortByLabel}
								setSortByLabel={setSortByLabel}
							/>
						</Box>

						<DataTable data={sortByLabel ? sortedData : data} />
					</>
				)}
			</Box>
		</Box>
	);
};

const SortMenu = ({ sortByLabel, setSortByLabel }) => {
	const onClose = (e, popupState) => {
		setSortByLabel(e.target.value);
		console.log(e);
		popupState.close();
	};
	return (
		<PopupState variant="popover" popupId="demo-popup-menu">
			{(popupState) => (
				<React.Fragment>
					<Button
						variant="contained"
						style={{ background: 'pink', color: 'black' }}
						{...bindTrigger(popupState)}
					>
						Sort By
					</Button>
					<Menu {...bindMenu(popupState)}>
						{desired_data_fields.map((c) => {
							return (
								<MenuItem>
									<div
										style={{ borderBottom: 'solid', width: '100%', height: 50 }}
										onClick={(e) => onClose(e, popupState)}
									>
										{c}
									</div>
								</MenuItem>
							);
						})}
					</Menu>
				</React.Fragment>
			)}
		</PopupState>
	);
};

const DataTable = ({ data }) => {
	const [page, setPage] = useState(0);
	const [rowsPerPage, setRowsPerPage] = useState(10);

	let sym = ['Symbol'];
	const columns = sym.concat(
		Object.keys(data[0][1]).filter((c) => {
			return desired_data_fields.includes(c);
		})
	);

	const handleChangePage = (event, newPage) => {
		setPage(newPage);
	};

	const handleChangeRowsPerPage = (event) => {
		setRowsPerPage(+event.target.value);
		setPage(0);
	};

	const renderRows = () => {
		return data
			.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
			.map((row) => {
				return (
					<TableRow>
						<TableCell
							style={{
								fontWeight: 'bold',
							}}
						>
							{row[1]['shortName']}
						</TableCell>
						{columns.slice(1, columns.length).map((c, idx) => {
							return (
								<TableCell
									style={{
										padding: 'none',
										background: 'white',
										height: '2rem',
									}}
									key={idx}
								>
									{row[1][c]}
								</TableCell>
							);
						})}
					</TableRow>
				);
			});
	};

	const renderHeader = () => {
		return columns.map((c, idx) => {
			return <TableCell key={idx}>{c}</TableCell>;
		});
	};
	return (
		<TableContainer
			style={{
				width: '80vw',
				height: '70vh',
				border: 'solid',
				borderRadius: 15,
				padding: '10 0 10 10',
				borderColor: 'gray',
				flex: 8,
			}}
		>
			<Table stickyHeader>
				<TableHead>
					<TableRow>{renderHeader()}</TableRow>
				</TableHead>

				<TableBody>{renderRows()}</TableBody>
				<TableFooter>
					<TableRow>
						<TablePagination
							rowsPerPageOptions={[5, 10, 25, 50, 100]}
							component="div"
							count={data.length}
							rowsPerPage={rowsPerPage}
							page={page}
							onPageChange={handleChangePage}
							onRowsPerPageChange={handleChangeRowsPerPage}
						/>
					</TableRow>
				</TableFooter>
			</Table>
		</TableContainer>
	);
};
